t;padding-bottom:10px!important}\r\n#header.sticky-header .header-main .container .header-left{padding-top:13px!important;padding-bottom:10px!important}\r\n#header .logo{position:relative;left:15px;top:-3px}\r\n#header.sticky-header .logo{top:-1px}\r\n#header.header-corporate .header-main .container .header-right{padding:0;display:table;width:100%}\r\n#header .header-main .container .header-right > div{display:table-cell!important;height:100%;padding:15px 0}\r\n#header.sticky-header .header-main .container .header-right > div{padding:14px 0}\r\n#header.header-corporate .share-links{position:relative;right:10px;top:-2px}\r\n#header.sticky-header.header-corporate .share-links{top:-1px}\r\n#header.header-corporate .share-links a{background-color:transparent;box-shadow:none;color:#fff}\r\n.header-left::after,#header .share-links:after{border-right:1px solid #5b5d61;content:\"\";height:100%;position:absolute;right:-6px;top:0}\r\n#header .share-links:after{left:17px;right:initial;top:3px}\r\n#header .header-main .container #main-menu{float:left;padding-left:14px;width:100%}\r\n#header.sticky-header #main-menu{position:relative;top:1px}\r\n#main-menu > ul{clear:left;float:left;left:50%;position:relative;top:-2px}\r\n#main-menu > ul > li{right:50%}\r\n#main-menu .mega-menu > li.menu-item > a,#main-menu .mega-menu > li.menu-item > h5{font-size:12px;font-weight:100;padding:9px 14.8px 8px}\r\n#main-menu .mega-menu > li.menu-item.active > a:before,#main-menu .mega-menu > li.menu-item.active > h5:before,#main-menu .mega-menu > li.current-menu-item > a:before{border-bottom:3px solid #019fdc;bottom:-16px;content:\"\";display:block;left:0;position:absolute;width:100%}\r\n}\r\n@media (max-width: 1170px) {\r\n.header-left:after, #header .share-links:after { right:-30px }\r\n#header .share-links:after{left:-5px;right:initial}\r\n}\r\n@media (max-width:992px) {\r\n    #header .logo{position:relative;top:-7px}\r\n    #header.sticky-header .logo{top:auto}\r\n}\r\n@media (max-width:575px) {\r\n#header.sticky-header .header-main .container{display:block;padding-bottom:15px;padding-top:15px}\r\n#header .mobile-toggle{left:auto;position:absolute;right:13px;top:17px}\r\n#header.sticky-header .mobile-toggle{top:10px}\r\n#header.sticky-header .logo{display:block}\r\n#header .logo{margin:0}\r\n}\r\n@media (max-width: 991px) {\r\n#header:not(.sticky-header){background:#3c3f44 none repeat scroll 0 0}\r\n#header .header-main{border-bottom:0}\r\n}\r\n\r\n/* Mobile menu */\r\n#nav-panel .mobile-menu li { border-bottom: none; }\r\n#nav-panel .mobile-menu > li.active { border-bottom: 2px solid #019fdc; }\r\n#nav-panel .accordion-menu > li.menu-item > a,\r\n#nav-panel .accordion-menu > li.menu-item > h5,\r\n#nav-panel .accordion-menu > li.menu-item > .arrow { color: #fff; }\r\n#nav-panel .mobile-menu > li > a,\r\n#nav-panel .mobile-menu > li > h5 { font-weight: 100; }\r\n#nav-panel .accordion-menu li.menu-item.active > a,\r\n#nav-panel .accordion-menu li.menu-item.active > h5 { background: none; }\r\n\r\n/* page */\r\n.pagination.load-more .next { font-family: inherit; text-transform: uppercase; }\r\n/* Blog */\r\n.owl-carousel.show-nav-title .owl-nav [class*=\"owl-\"],.owl-carousel.show-nav-title .owl-nav [class*=\"owl-\"]:hover,.owl-carousel.show-nav-title .owl-nav [class*=\"owl-\"]:active{color:#2e353e;font-size:30px}\r\n.post-carousel .post-item{background:#fff none repeat scroll 0 0}\r\n.post-recent-main .post-meta i{display:none}\r\n.post-item.style-2 .post-image.m-b-md{margin:0!important}\r\n.post-recent-main{padding:30px;position:relative}\r\n.post-recent-main .meta-date{background-color:#2e353e;color:#fff;right:0;top:0;font-size:10px;letter-spacing:1.5px;line-height:1;padding:8px 13px 5px;position:absolute;right:12px;top:-12px;z-index:5}\r\n.post-meta .meta-cats > a{color:#777;font-size:10px;letter-spacing:1.5px;pointer-events:none;text-transform:uppercase}\r\n.post-carousel .post-item .post-meta,.post-grid .post-item .post-meta,.post-timeline .post-item 