 */\r\n#header.header-11 .header-main:not(.sticky) > .container {\r\n  border-bottom: 1px solid rgba(255, 255, 255, 0.12);\r\n}\r\n\r\nbody:not(.home) #header.header-11 .header-main .container .header-left {\r\n  padding-top: 7px;\r\n}\r\n\r\n#header.header-11 .header-main #main-menu .mega-menu > li.menu-item {\r\n  margin-left: 2px;\r\n}\r\n\r\n#header.header-11 .header-main #main-menu .mega-menu > li.menu-item > a, #header.header-11 .header-main #main-menu .mega-menu > li.menu-item > h5 {\r\n  padding: 35px 20px 30px;\r\n  border-top: none !important;\r\n}\r\n\r\n#header.header-11 .share-links {\r\n  display: none;\r\n}\r\n\r\n.fixed-header #header.sticky-header .header-main {\r\n  padding-top: 4px;\r\n}\r\n\r\n.mega-menu.effect-down > li.menu-item .popup {\r\n  top: auto;\r\n  opacity: 0;\r\n  visibility: hidden;\r\n  -webkit-transform: scale(0.7);\r\n  -moz-transform: scale(0.7);\r\n  -ms-transform: scale(0.7);\r\n  -o-transform: scale(0.7);\r\n  transform: scale(0.7);\r\n  -webkit-transition: ease all 300ms;\r\n  moz-transition: ease all 300ms;\r\n  transition: ease all 300ms;\r\n}\r\n\r\n.mega-menu.effect-down > li.menu-item:hover .popup {\r\n  opacity: 1;\r\n  visibility: visible;\r\n  -webkit-transform: scale(1);\r\n  -moz-transform: scale(1);\r\n  -ms-transform: scale(1);\r\n  -o-transform: scale(1);\r\n  transform: scale(1);\r\n  -webkit-transition: ease all 300ms;\r\n  -moz-transition: ease all 300ms;\r\n  transition: ease all 300ms;\r\n}\r\n\r\n#nav-panel .mobile-nav-wrap {\r\n  padding: 10px !important;\r\n}\r\n\r\n/* Page Title & Breadcrumb */\r\n.page-top {\r\n  padding-bottom: 8px;\r\n}\r\n\r\n.page-top .page-title {\r\n  font-size: 2em;\r\n}\r\n\r\n.page-top .page-title:before {\r\n  content: '- ';\r\n}\r\n\r\n.page-top .page-sub-title {\r\n  font-size: 1.2em;\r\n  opacity: 0.65;\r\n}\r\n\r\n.page-top ul.breadcrumb > li, .page-top ul.breadcrumb > li > a {\r\n  text-transform: uppercase;\r\n  font-size: .98em;\r\n}\r\n\r\n/* List */\r\nhtml .list-tertiary.list-icons.list-icons-style-3 li .fa {\r\n  padding: 10px;\r\n  padding-top: 8px;\r\n  color: #777;\r\n  font-size: 10px;\r\n}\r\n\r\n/* Home */\r\n.custom-hyphen {\r\n  position: relative;\r\n}\r\n\r\n.custom-hyphen:before {\r\n  content: '';\r\n  position: absolute;\r\n  border-top: 2px solid #fff;\r\n  width: 12px;\r\n  left: 1px;\r\n  top: 10px;\r\n}\r\n\r\n.letter-spacing-normal {\r\n  letter-spacing: normal !important;\r\n}\r\n\r\n.custom-line-height-22 p {\r\n  line-height: 22px;\r\n}\r\n\r\n.custom-line-height-23 p {\r\n  line-height: 23px;\r\n}\r\n\r\n.custom-position-1 {\r\n  margin-top: -197px;\r\n}\r\n\r\n.custom-call-to-action .action-info {\r\n  font-size: 1.125rem;\r\n}\r\n\r\n.btn.btn-lg {\r\n  padding: 12px 30px !important;\r\n  border-width: 2px;\r\n  font-size: 14px !important;\r\n}\r\n\r\n.stats-text {\r\n  letter-spacing: -1px;\r\n}\r\n\r\n.custom-counters-column:before {\r\n  content: '';\r\n  display: block;\r\n  position: absolute;\r\n  top: 50%;\r\n  left: 50%;\r\n  box-shadow: 0 0 97px 70px rgba(152, 152, 152, 0.1);\r\n  -webkit-transform: translate(-50%, -50%);\r\n  -moz-transform: translate(-50%, -50%);\r\n  -ms-transform: translate(-50%, -50%);\r\n  -o-transform: translate(-50%, -50%);\r\n  transform: translate(-50%, -50%);\r\n}\r\n\r\n.stats-left .porto-sicon-left {\r\n  padding-top: 3px;\r\n  padding-right: 20px;\r\n}\r\n\r\n/* Expertise */\r\n.porto-sicon-box {\r\n  margin-bottom: 0;\r\n}\r\n\r\n.custom-info-box .porto-sicon-img {\r\n  position: relative;\r\n  margin-top: 0;\r\n  margin-right: 20px !important;\r\n  padding: 5px;\r\n  width: 78px !important;\r\n  height: 78px !important;\r\n  border: 6px solid #FFF;\r\n  border-radius: 100%;\r\n  background: transparent;\r\n  overflow: hidden;\r\n  z-index: 1;\r\n  -webkit-transition: ease box-shadow 300ms;\r\n  -moz-transition: ease box-shadow 300ms;\r\n  transition: ease box-shadow 300ms;\r\n}\r\n\r\n.custom-info-box.custom-box-shadow .porto-sicon-box .porto-sicon-img, .custom-