20px !important;\r\n}\r\n\r\n.menu-item.custom-long-arrow-right a {\r\n  line-height: 18px !important;\r\n}\r\n\r\n#nav-panel .accordion-menu li.menu-item:hover > a {\r\n  background: transparent;\r\n}\r\n\r\n.tp-caption img {\r\n  transition: transform 0.3s ease 0 !important;\r\n  will-change: transform;\r\n  cursor: pointer;\r\n}\r\n\r\n.tp-caption img:hover {\r\n  transform: scale(1.1, 1.1);\r\n}\r\n\r\n/* Mobile menu */\r\n#nav-panel .mobile-nav-wrap {\r\n  padding: 10px !important;\r\n}\r\n\r\n#nav-panel .accordion-menu > li.menu-item > a,\r\n#nav-panel .accordion-menu > li.menu-item > h5,\r\n#nav-panel .accordion-menu > li.menu-item > .arrow {\r\n  color: inherit;\r\n}\r\n\r\n#nav-panel .accordion-menu li.menu-item.active > a,\r\n#nav-panel .accordion-menu li.menu-item.active > h5 {\r\n  background: none;\r\n  color: #43a6a3;\r\n}\r\n\r\n#nav-panel .menu-item.custom-long-arrow-right {\r\n  margin: 8px 0 !important;\r\n}\r\n\r\n/* Buy Tickets Button Start */\r\n.custom-b-r-lg {\r\n  border-radius: 30px !important;\r\n}\r\n\r\n.menu-item.font-weight-bold a {\r\n  font-weight: 700 !important;\r\n}\r\n\r\n.menu-item.text-color-light a {\r\n  color: #fff !important;\r\n}\r\n\r\n.custom-long-arrow-right {\r\n  position: relative;\r\n  padding: 0 !important;\r\n}\r\n\r\n.custom-long-arrow-right a, a.custom-long-arrow-right, .custom-button-long-arrow-right button, .custom-button-long-arrow-right a {\r\n  padding-right: 74px !important;\r\n  padding-left: 30px !important;\r\n  margin-left: 0 !important;\r\n  font-size: 1em !important;\r\n  webkit-transition: ease padding 300ms;\r\n  -moz-transition: ease padding 300ms;\r\n  transition: ease padding 300ms;\r\n}\r\n\r\na.custom-long-arrow-right {\r\n  padding-top: 10px !important;\r\n  padding-bottom: 10px !important;\r\n}\r\n\r\n.custom-button-long-arrow-right button, .custom-button-long-arrow-right a {\r\n  position: relative;\r\n  padding-top: 10.5px !important;\r\n  padding-bottom: 10.5px !important;\r\n}\r\n\r\n.custom-button-long-arrow-right button.vc_btn3-style-outline-custom, .custom-button-long-arrow-right a.vc_btn3-style-outline-custom {\r\n  border-width: 1px !important;\r\n}\r\n\r\n.custom-long-arrow-right:hover a, a.custom-long-arrow-right:hover, .custom-button-long-arrow-right:hover button, .custom-button-long-arrow-right:hover a {\r\n  padding-right: 80px !important;\r\n}\r\n\r\n.custom-long-arrow-right:before, .custom-button-long-arrow-right button:before, .custom-button-long-arrow-right a:before {\r\n  content: '';\r\n  display: block;\r\n  position: absolute;\r\n  top: 50.5%;\r\n  right: 31px;\r\n  width: 25px;\r\n  border-bottom: 1px solid #FFF;\r\n  webkit-transition: ease width 300ms;\r\n  -moz-transition: ease width 300ms;\r\n  transition: ease width 300ms;\r\n}\r\n\r\n.custom-long-arrow-right:after, .custom-button-long-arrow-right button:after, .custom-button-long-arrow-right a:after {\r\n  content: '';\r\n  position: absolute;\r\n  top: 43%;\r\n  right: 31px;\r\n  border-top: 4px solid transparent;\r\n  border-bottom: 4px solid transparent;\r\n  border-left: 4px solid #FFF;\r\n}\r\n\r\n.custom-button-long-arrow-right button:after {\r\n  top: 42%;\r\n}\r\n\r\n.custom-long-arrow-right:hover:before, .custom-button-long-arrow-right button:hover:before, .custom-button-long-arrow-right a:hover:before {\r\n  width: 30px;\r\n}\r\n\r\n/* About US */\r\n.custom-play-video {\r\n  position: relative;\r\n  height: 100%;\r\n}\r\n\r\n.custom-play-video img {\r\n  position: absolute;\r\n  top: 50%;\r\n  left: 50%;\r\n  width: 90px;\r\n  margin-left: -45px;\r\n  margin-top: -45px;\r\n  will-change: transform;\r\n  -webkit-transition: -webkit-transform .3s ease;\r\n  -moz-transition: -moz-transform .3s ease;\r\n  transition: transform .3s ease;\r\n}\r\n\r\n.custom-play-video img:hover {\r\n  webkit-transform: scale(1.1, 1.1);\r\n  -moz-transform: scale(1.1, 1.1);\r\n  -ms-transform: scale(1.1, 1.1);\r\n  -o-transform: scale(1.1, 1.1);\r\n  transform: scale(1.1, 1.1);\r\n}\r\n