lumn-inner {\r\n  padding: 26px 0 21px 11px !important;\r\n}\r\n\r\n.custom-about-me-links a {\r\n  letter-spacing: -.5px;\r\n  margin-left: -10px;\r\n}\r\n\r\n.custom-about-me-links .left-icon .porto-sicon-left .porto-icon {\r\n  background: transparent !important;\r\n  margin-right: 4px;\r\n}\r\n\r\n/* Skills */\r\n.progress.progress-sm, .vc_progress_bar .vc_single_bar.progress.progress-sm, .vc_progress_bar .progress-label {\r\n  display: inline-block;\r\n}\r\n\r\n.vc_progress_bar .vc_single_bar.progress.progress-sm, .progress.progress-sm {\r\n  margin-top: 10px;\r\n  margin-bottom: 15px;\r\n  width: 70% !important;\r\n  height: 3px;\r\n}\r\n\r\n.vc_progress_bar .progress-label {\r\n  float: left;\r\n  width: 30%;\r\n  font-size: 0.8125rem;\r\n  font-weight: 600;\r\n  color: #2e353e;\r\n}\r\n\r\n.custom-box-details {\r\n  padding: 35px;\r\n  box-shadow: 0 30px 120px 0 #dde5ee;\r\n}\r\n\r\n.about-custom-box-details {\r\n  padding-bottom: 50px;\r\n}\r\n\r\n@media (min-width: 992px) {\r\n  .custom-md-margin-top-1 {\r\n    margin-top: 58px;\r\n  }\r\n}\r\n.custom-list-style-1 {\r\n  padding: 0;\r\n}\r\n\r\n.custom-list-style-1 li {\r\n  list-style: none;\r\n}\r\n\r\n.custom-list-style-1 li span {\r\n  text-transform: uppercase;\r\n  font-weight: 600;\r\n  font-size: .9em;\r\n}\r\n\r\n.custom-list-style-1 li span.custom-max-width-1 {\r\n  min-width: 130px;\r\n}\r\n\r\n/* Advance Carousel navigation */\r\n.slick-slider .slick-dots li i {\r\n  display: none;\r\n}\r\n\r\n.slick-slider .slick-dots li {\r\n  position: relative;\r\n  width: 12px;\r\n  height: 12px;\r\n  margin: 5px 2px;\r\n  background: transparent !important;\r\n  border: 2px solid #2e353e;\r\n  border-radius: 50%;\r\n}\r\n\r\n.slick-slider .slick-dots li:hover, .slick-slider .slick-dots li.slick-active {\r\n  border-color: #2e353e !important;\r\n  background-color: #2e353e !important;\r\n}\r\n\r\n.custom-box-shadow .slick-list {\r\n  padding-bottom: 40px;\r\n}\r\n\r\n.custom-box-shadow .porto-ultimate-cont